INSERT INTO `zyz_article` VALUES ('713', '配置的应用', '配置的应用', '配置的应用', '<p>&lt;?php\r\n</p><p>namespace&nbsp;app\\admin\\controller;\r\n</p><p>use&nbsp;think\\facade\\Config;\r\n</p><p>class&nbsp;User\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;get(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取所有配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//仅获取app下面的配置项，app是一级配置项，与config/app.php文件对应\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'app.\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取一级配置项，更推荐使用pull()\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::pull(\'app\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取二级配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'app.app_debug\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//app是默认的以及配置前缀，所以可以省略\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'app_debug\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//查询某配置项是否存在\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::has(\'app_debug\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//查询database一级配置项下的内容\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'database.hostname\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;set()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态设置，静态设置为直接修改配置文件\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态设置用的是Config类中的set()方法\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'app_debug\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Config::set(\'app_debug\',true);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Config::get(\'app_debug\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//助手函数\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;helper()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//助手函数不依赖与Config类,所以可以注释掉use&nbsp;think\\facade\\Config;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(config());//不传入参数就是获取全部的配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(config(\'default_module\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//当前是否存在某配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(config(\'?database.username\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//查询某配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(config(\'database.hostname\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//设置某配置项\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(config(\'database.hostname\',\'localhost\'));\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p>', '1', '1', '183', '1', '1540632543', '1540634778');
INSERT INTO `zyz_article` VALUES ('714', '单例模式、工厂模式、注册树的结合应用', '单例模式、工厂模式、注册树的结合应用', '单例模式、工厂模式、注册树的结合应用', '<p><font face=\"微软雅黑\">非thinkphp5.1框架，框架中的容器可以理解为注册池</font></p><p><font face=\"微软雅黑\">可直接复制运行查看结果</font></p><p>&lt;?php\r\n</p><p>/**\r\n</p><p>*单例模式/工厂模式是怎么生成实例\r\n</p><p>*注册树模式是怎么用实例\r\n</p><p>*/\r\n</p><p>/**\r\n</p><p>*单例模式\r\n</p><p>*/\r\n</p><p>class&nbsp;Site\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;$siteName;\r\n</p><p>&nbsp;&nbsp;&nbsp;//本类静态实例\r\n</p><p>&nbsp;&nbsp;&nbsp;protected&nbsp;static&nbsp;$instance&nbsp;=&nbsp;null;\r\n</p><p>&nbsp;&nbsp;&nbsp;//禁用构造器\r\n</p><p>&nbsp;&nbsp;&nbsp;private&nbsp;function&nbsp;__construct($siteName){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;siteName&nbsp;=&nbsp;$siteName;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//获取本类唯一实例\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;function&nbsp;getInstance($siteName&nbsp;=&nbsp;\'知丫仔\')\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(!self::$instance&nbsp;instanceof&nbsp;self){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;self::$instance&nbsp;=&nbsp;new&nbsp;self($siteName);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;self::$instance;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>//用工厂模式来生成本类的单一实例\r\n</p><p>class&nbsp;Factory\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//创建指定类的实例\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;function&nbsp;create()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Site::getInstance(\'www.zhiyazai.cn\');\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;\r\n</p><p>}\r\n</p><p>/**\r\n</p><p>*对象注册树\r\n</p><p>*1、注册：set(),把对象注册到树上\r\n</p><p>*2、获取:get(),把对象取下来用\r\n</p><p>*3、注销：_unset():把对象吃掉\r\n</p><p>*/\r\n</p><p>class&nbsp;Register\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//创建对象池\r\n</p><p>&nbsp;&nbsp;&nbsp;protected&nbsp;static&nbsp;$objects&nbsp;=&nbsp;[];\r\n</p><p>&nbsp;&nbsp;&nbsp;//生成对象并上树\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;function&nbsp;set($alias,$object){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;self::$objects[$alias]&nbsp;=&nbsp;$object;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//从树上面取下对象\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;function&nbsp;get($alias){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;self::$objects[$alias];\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//注销对象\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;static&nbsp;function&nbsp;_unset($alias){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;unset(self::$objects[$alias]);\r\n</p><p>&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;\r\n</p><p>}\r\n</p><p>//将Site类的实例上树，放到对象池\r\n</p><p>Register::set(\'site\',Factory::create());\r\n</p><p>//从树上取一个对象下来\r\n</p><p>$obj&nbsp;=&nbsp;Register::get(\'site\');\r\n</p><p>var_dump($obj);\r\n</p><p>echo&nbsp;\"&lt;hr&gt;\";\r\n</p><p>echo&nbsp;$obj-&gt;siteName;</p><p><br></p>', '2', '1', '183', '1', '1540634770', '1540634770');
INSERT INTO `zyz_article` VALUES ('715', 'php中 trait 的用法一', 'php中 trait 的用法一', 'php中 trait 的用法一', '<p>trait实现了代码的复用并突破了单继承的限制，trait类不是类，不能实例化</p><p>可直接复制运行查看结果</p><p>&lt;?php\r\n</p><p>/**\r\n</p><p>*trait实现了代码的复用\r\n</p><p>*并且突破了单继承的限制\r\n</p><p>*trait是类不是类，不能实例化\r\n</p><p>*/\r\n</p><p>trait&nbsp;Demo1\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello1(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>trait&nbsp;Demo2\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello2(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>class&nbsp;Demo\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;use&nbsp;Demo1,Demo2;\r\n</p><p>&nbsp;&nbsp;&nbsp;/*\r\n</p><p>&nbsp;&nbsp;&nbsp;*use&nbsp;Demo1,Demo2等价于将以下方法复制到了该处\r\n</p><p>&nbsp;&nbsp;&nbsp;*public&nbsp;function&nbsp;hello1(){\r\n</p><p>&nbsp;&nbsp;&nbsp;*&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;*}\r\n</p><p>&nbsp;&nbsp;&nbsp;*\r\n</p><p>&nbsp;&nbsp;&nbsp;*public&nbsp;function&nbsp;hello2(){\r\n</p><p>&nbsp;&nbsp;&nbsp;*&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;*}\r\n</p><p>&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test1(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;hello1();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test2(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;hello2();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>$obj&nbsp;=&nbsp;new&nbsp;Demo;\r\n</p><p>echo&nbsp;$obj-&gt;hello();\r\n</p><p>echo&nbsp;\'&lt;hr&gt;\';\r\n</p><p>echo&nbsp;$obj-&gt;test1();\r\n</p><p>echo&nbsp;\'&lt;hr&gt;\';\r\n</p><p>echo&nbsp;$obj-&gt;test2();</p><p><br></p>', '3', '1', '183', '1', '1540634901', '1540634901');
INSERT INTO `zyz_article` VALUES ('716', 'php中 trait 的用法二', 'php中 trait 的用法二', 'php中 trait 的用法二', '<p>trait 优先级的问题，当类中的方法与 trait 类，父类中的方法重名了，怎么办？</p><p>trait类的优先级高于父类</p><p>可直接复制运行查看结果</p><p>&lt;?php\r\n</p><p>/**\r\n</p><p>*trait优先级的问题\r\n</p><p>*1、当前类中的方法与trait类，父类中的方法重名了，怎么办？\r\n</p><p>*trait类的优先级高于父类\r\n</p><p>*/\r\n</p><p>trait&nbsp;Demo1\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>class&nbsp;Test{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>class&nbsp;Demo&nbsp;extends&nbsp;Test\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;use&nbsp;Demo1;\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;public&nbsp;function&nbsp;hello()\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;}\r\n</p><p>}\r\n</p><p>$obj&nbsp;=&nbsp;new&nbsp;Demo;\r\n</p><p>echo&nbsp;$obj-&gt;hello();</p><p><br></p>', '4', '1', '183', '1', '1540634966', '1540634966');
INSERT INTO `zyz_article` VALUES ('717', 'php中 trait 的用法三', 'php中 trait 的用法三', 'php中 trait 的用法三', '<p>trait 优先级的问题</p><p>当多个trait类中有同名的方法，怎么办？</p><p>可直接复制运行查看结果</p><p>&lt;?php\r\n</p><p>/**\r\n</p><p>*trait优先级的问题\r\n</p><p>*当多个trait类中有同名的方法，怎么办？\r\n</p><p>*/\r\n</p><p>trait&nbsp;Demo1\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>trait&nbsp;Demo2\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>class&nbsp;Test{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>class&nbsp;Demo&nbsp;extends&nbsp;Test\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;use&nbsp;Demo1,Demo2{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Demo1::hello&nbsp;insteadof&nbsp;Demo2;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Demo2::hello&nbsp;as&nbsp;Demo2Hello;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;public&nbsp;function&nbsp;hello()\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;__METHOD__;\r\n</p><p>&nbsp;&nbsp;&nbsp;//&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;Demo2Hello();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}\r\n</p><p>$obj&nbsp;=&nbsp;new&nbsp;Demo;\r\n</p><p>echo&nbsp;$obj-&gt;hello();\r\n</p><p>echo&nbsp;\'&lt;hr&gt;\';\r\n</p><p>echo&nbsp;$obj-&gt;test();</p><p><br></p>', '5', '1', '183', '1', '1540635031', '1540635031');
INSERT INTO `zyz_article` VALUES ('718', '容器（注册树）', '容器（注册树）', '容器（注册树）', '<p>容器与依赖注入的原理</p><p>请通过命名空间和类名自行确定文件的路径</p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\common;\r\n</p><p>class&nbsp;Temp\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;\r\n</p><p>&nbsp;&nbsp;&nbsp;private&nbsp;$name;\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;__construct($name=\'Peter\'){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;name&nbsp;=&nbsp;$name;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;setName($name)\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;name&nbsp;=&nbsp;$name;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;getName(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'方法是：\'.__METHOD__.\'属性是：\'.$this-&gt;name;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>/**\r\n</p><p>*容器与依赖注入的原理\r\n</p><p>*-------------------------------------\r\n</p><p>*1、任何的url访问，最终都是定位到控制器，由控制器中某个具体的方法去执行\r\n</p><p>*2、一个控制器对应着一个类，如果这些类需要进行统一管理，怎么办？\r\n</p><p>*用容器来进行类管理，还可以将类的实例（对象）作为参数，传递给类方法，自动触发依赖注入\r\n</p><p>*依赖注入：将对象类型的数据，以参数的方式传到方法的参数中\r\n</p><p>*url访问：通过GET方式将数据传到控制器指定的方法中，但是只能传字符串，数值，那如果要传一个对象到当前方法中怎么办？\r\n</p><p>*依赖注入：解决了向类中的方法传对象的问题\r\n</p><p>********/\r\n</p><p>class&nbsp;Demo1\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//可以通过字符串、数值用get方式来传值到类方法中\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;getName($name&nbsp;=&nbsp;\'Peter\')\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'Hello&nbsp;\'.$name;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;*\\app\\common\\Temp&nbsp;$temp:将会触发依赖注入\r\n</p><p>&nbsp;&nbsp;&nbsp;*等价于&nbsp;&nbsp;&nbsp;\r\n</p><p>&nbsp;&nbsp;&nbsp;*$temp&nbsp;=&nbsp;new&nbsp;\\app\\common\\Temp;\r\n</p><p>&nbsp;&nbsp;&nbsp;*\r\n</p><p>&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;getMethod(\\app\\common\\Temp&nbsp;$temp)\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$temp-&gt;setName(\'知丫仔\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$temp-&gt;getName();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//绑定一个类到容器\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;bindClass()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//把一个类放到容器中：相当于注册到容器中\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\think\\Container::set(\'temp\',\'\\app\\common\\Temp\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//使用助手函数bind()\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//bind(\'temp\',\'\\app\\common\\Temp\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//将容器中的类实例化并取出来用：实例化时同调用构造器进行初始化\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$temp&nbsp;=&nbsp;\\think\\Container::get(\'temp\',[\'name\'=&gt;\'Peter_Zhu\']);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//回应上面的助手函数\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//$temp&nbsp;=&nbsp;app(\'temp\',[\'name\'=&gt;\'Peter_Zhu\']);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$temp-&gt;getName();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//绑定一个闭包到容器：闭包现在请理解为一个匿名函数\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;bindClosure()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//把一个闭包放到容器中：相当于注册到容器中\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\think\\Container::set(\'demo\',function($domain){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'知丫仔是：\'.$domain;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//将容器中的闭包取出来用\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$temp&nbsp;=&nbsp;\\think\\Container::get(\'demo\',[\'domain\'=&gt;\'www.zhiyazai.cn\']);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//回应上面的助手函数\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//$temp&nbsp;=&nbsp;app(\'temp\',[\'name\'=&gt;\'Peter_Zhu\']);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$temp;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p><br></p>', '6', '1', '183', '1', '1540635106', '1540635106');
INSERT INTO `zyz_article` VALUES ('719', 'facade 门面：静态代理', 'facade 门面：静态代理', 'facade 门面：静态代理', '<p>请通过命名空间和类名自行确定文件的路径</p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\common;\r\n</p><p>class&nbsp;Test\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;hello($name)\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'Hello&nbsp;\'.$name;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\facade;\r\n</p><p>class&nbsp;Test&nbsp;extends&nbsp;\\think\\Facade\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;*代理了类app\\common\\Test,给要调用的类一个静态马甲\r\n</p><p>&nbsp;&nbsp;&nbsp;**/\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;protected&nbsp;static&nbsp;function&nbsp;getFacadeClass()\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'app\\common\\Test\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;app\\facade\\Test;\r\n</p><p>class&nbsp;Demo2\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;index($name&nbsp;=&nbsp;\'ThinkPHP\')\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//常规访问\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;$test&nbsp;=&nbsp;new&nbsp;\\app\\common\\Test();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;return&nbsp;$test-&gt;hello($name);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*如果想静态调用一个动态方法，需要给当前的类绑定一个静态代理的类\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Test::hello(\'Peter_Zhu\');\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;*如果想让静态代理类app\\facade\\Test1同框架的静态代理类（例:think\\facade\\App.php）一致，app\\facade\\Test1静态代理类中没有显示指定要绑定的类名，只有一段继承代码，则需要动态绑定\r\n</p><p>&nbsp;&nbsp;&nbsp;*\\think\\Facade::bind();\r\n</p><p>&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;index1($name&nbsp;=&nbsp;\'ThinkPHP\'){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//动态绑定\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\\think\\Facade::bind(\'app\\facade\\Test1\',\'app\\common\\Test\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Test::hello(\'Peter_Zhu\');\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\facade;\r\n</p><p>class&nbsp;Test1&nbsp;extends&nbsp;\\think\\Facade\r\n</p><p>{\r\n</p><p>}</p><p><br></p>', '7', '1', '183', '1', '1540635223', '1540635223');
INSERT INTO `zyz_article` VALUES ('720', 'Request请求对象', 'Request请求对象', 'Request请求对象', '<p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;think\\facade\\Request;//导入请求对象的静态代理\r\n</p><p>/**\r\n</p><p>*正常情况下，控制器不依赖于父类controller.php\r\n</p><p>*推荐继承父类，可以很方便的使用在父类中封装好的一些方法和属性\r\n</p><p>*controller.php&nbsp;没有静态代理\r\n</p><p>*控制器中的输出，字符串全部用return&nbsp;返回，不要用echo\r\n</p><p>*如果输出的是复杂类型，我们可以用dump()函数\r\n</p><p>*默认输出的格式为html,可以指定为其它格式：json\r\n</p><p>*\r\n</p><p>*1、传统的new&nbsp;Request\r\n</p><p>*2、静态代理：think\\facade\\Request\r\n</p><p>*3、依赖注入：Request&nbsp;$request\r\n</p><p>*4、父类controller中的属性$request:$this-&gt;request\r\n</p><p>*/\r\n</p><p>class&nbsp;Demo3&nbsp;extends&nbsp;\\think\\Controller\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//创建一个请求对象的Request的静态代理\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//get的访问方式为：http://tp51.com/index/Demo3/test?name=jiaoweijia\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Request::get());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*传统方式：\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*use&nbsp;think\\Request;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*$request&nbsp;=&nbsp;new&nbsp;Request();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*dump($request-&gt;get());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*依赖注入的方法：\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*use&nbsp;think\\Request;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*public&nbsp;functon&nbsp;test(Request&nbsp;$request){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;dump($request-&gt;get());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*}\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*其它的方法：\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*use&nbsp;think\\Request;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*use&nbsp;think\\Controller;\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*public&nbsp;functon&nbsp;test(){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;dump($this-&gt;request-&gt;get());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;//$this-&gt;request等价于new&nbsp;Request();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*}\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p>', '8', '1', '183', '1', '1540635252', '1540635252');
INSERT INTO `zyz_article` VALUES ('721', '数据库的入库', '数据库的入库', '数据库的入库', '<p>think\\Db.php中只有一个静态调用__callStatic</p><p><b>1、数据库常用的三种配置方式</b></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;think\\Db;\r\n</p><p>/**\r\n</p><p>*链接数据库\r\n</p><p>*1、全局配置：config/database.php\r\n</p><p>*2、动态配置：think\\db\\Query.php中有一个方法：connect()\r\n</p><p>*3、DSN链接：数据库类型://用户名:密码@数据库地址:端口号/数据库的名称#字符集\r\n</p><p>*/\r\n</p><p>class&nbsp;Demo4\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//测试的全局配置：config/database.php\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;conn1()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',1)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;value(\'name\');\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//动态配置\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;conn2()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::connect([\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'type\'=&gt;\'mysql\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'hostname\'=&gt;\'127.0.0.1\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'database\'=&gt;\'test\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'username\'=&gt;\'root\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'password\'=&gt;\'root\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;])\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;value(\'name\');&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//DSN链接\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;conn3()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$dsn&nbsp;=&nbsp;\'mysql://root:root@127.0.0.1:3306/test#utf8\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::connect($dsn)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;value(\'name\');\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p><br></p><p><b>2、系统了解数据库的增删改查</b></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;think\\Db;\r\n</p><p>/**\r\n</p><p>*&nbsp;查询构造器\r\n</p><p>*&nbsp;准备工作：app_debug=&gt;\'true\';app_trace=&gt;\'true\';\r\n</p><p>*&nbsp;系统学习数据库的增删改查：CURD\r\n</p><p>*/\r\n</p><p>class&nbsp;Demo5\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//1、单条查询\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;find()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;Db类数据库操作的入口类\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;功能：静态调用think\\db\\Query.php类中的查询方法实现基本操作\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;table()：选择数据表\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;field():&nbsp;用来设置返回的字段数量或别名\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;where():&nbsp;设置查询条件&nbsp;表达式，数组\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;1、对于单个条件使用表达式\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;2、对于多个条件使用数组\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;find()返回符合条件的第一条记录，没有的话返回NULL\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$res&nbsp;=&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//-&gt;field(\'id,name,email\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;field([\'id\'=&gt;\'编号\',\'name\'=&gt;\'姓名\',\'email\'=&gt;\'邮箱\'])\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',\'=\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//-&gt;where(\'id\',2)//如果是相等关系等号是可以省略的-&gt;where(\'id\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;find();//如果是主键查询可以省略where,直接find(2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(is_null($res)?\'没有找到\':$res);\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//2、多条查询\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;select()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//select()返回的是一个二维数组，没有数据返回是一个空数据\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$res&nbsp;=&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;field(\'id,name,course\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where([\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[\'course\',\'=\',\'php\'],\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[\'grade\',\'&gt;=\',80]\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;])-&gt;select();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(empty($res)){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;\'没有满足条件的记录\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;foreach($res&nbsp;as&nbsp;$row){\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump($row);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//单条插入\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;insert()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//insert()成功返回新增的数量，失败返回false\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$data&nbsp;=&nbsp;[\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'name\'=&gt;\'黄老邪\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'email\'=&gt;\'hanglaoxie@qq.com\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'course\'=&gt;\'mysql\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'grade\'=&gt;\'80\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'create_time\'=&gt;time(),\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'update_time\'=&gt;time(),\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;];\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;insert($data);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//以replace&nbsp;insert的方式，只有mysql的时候，才可以传入true\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;insert($data,true);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//用了data()后，insert()中不能再有其它参数\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;data($data)-&gt;insert();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//插入的同时返回新增的主键id\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//insertGetId()同时执行两步：第一步插入，第二步返回主键id\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;insertGetId($data);\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//多条插入\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;insertAll()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$data&nbsp;=&nbsp;[\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[\'name\'=&gt;\'洪七公\',\'email\'=&gt;\'hongqigong@qq.com\'],\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;[\'name\'=&gt;\'欧阳锋\',\'email\'=&gt;\'ouyangfeng@qq.com\'],\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;];\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;insertAll($data);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')-&gt;data($data)-&gt;insertAll();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//更新操作\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;update()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//update()更新操作必须要有更新条件成功返回1，失败返回0，不能用data()方法\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//return&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//-&gt;where(\'id\',4)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//-&gt;update([\'name\'=&gt;\'南帝\']);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//如果更新条件是主键的话，可以直接把主键写到更新数组中\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;update([\'name\'=&gt;\'北丐\',\'id\'=&gt;2]);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//删除操作\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;delete()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;return&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;-&gt;delete(5);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::table(\'zh_student\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',4)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;delete();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//原生查询\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;query()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$sql&nbsp;=&nbsp;\"select&nbsp;name,email&nbsp;from&nbsp;zh_student&nbsp;where&nbsp;id&nbsp;in&nbsp;(1,2,20)\";\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Db::query($sql));\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//原生写操作：更新、删除、添加\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;execute()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;$sql&nbsp;=&nbsp;\"update&nbsp;zh_student&nbsp;set&nbsp;`name`&nbsp;=&nbsp;\'周伯\'&nbsp;where&nbsp;id&nbsp;=&nbsp;3\";\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;return&nbsp;Db::execute($sql);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//$sql&nbsp;=&nbsp;\"insert&nbsp;`zh_student`&nbsp;set&nbsp;`name`=\'五侠\'\";\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//return&nbsp;Db::execute($sql);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$sql&nbsp;=&nbsp;\"delete&nbsp;from&nbsp;`zh_student`&nbsp;where&nbsp;`name`=\'五侠\'\";\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;Db::execute($sql);&nbsp;\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p><p><br></p><p><br></p><p><b>3、模型</b></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\model;\r\n</p><p>use&nbsp;think\\Model;\r\n</p><p>class&nbsp;Student&nbsp;extends&nbsp;Model\r\n</p><p>{\r\n</p><p>}</p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;app\\index\\model\\Student;\r\n</p><p>//模型是和一张数据表绑定的\r\n</p><p>class&nbsp;Demo6\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//获取单条记录\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;get()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//dump(Student::get(3));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//更多时候用下面的方法\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//用查询构造器创建更加复杂的查询\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//返回的是Student对象\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$res&nbsp;=&nbsp;Student::field(\'id,name,email\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;find();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//返回的是数组\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;$res&nbsp;=&nbsp;Db::table(\'student\')-&gt;field(\'id,name,email\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;-&gt;where(\'id\',2)\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;-&gt;find();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;Db::table(\'student\')-&gt;&nbsp;等价于&nbsp;Student::\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump($res);\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//获取多条数据\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;all()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取全部\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Student::all());\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump(Student::all([1,2]));\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//用查询构造器创建更加复杂的查询\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$res&nbsp;=&nbsp;Student::field([\'name\'=&gt;\'姓名\',\'email\'=&gt;\'邮箱\'])\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;where(\'id\',\'in\',\'1,2,3\')\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;-&gt;select();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;dump($res);\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>}</p><p><br></p>', '9', '1', '183', '1', '1540635455', '1540635455');
INSERT INTO `zyz_article` VALUES ('722', '模板变量和常用标签', '模板变量和常用标签', '模板变量和常用标签', '<p>包含模板复制和变量输出、外部资源导入与格式化输出、循环判断与分页输出</p><p>后端：</p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\model;\r\n</p><p>use&nbsp;think\\Model;\r\n</p><p>class&nbsp;Student&nbsp;extends&nbsp;Model\r\n</p><p>{\r\n</p><p>}</p><p><br></p><p>&lt;?php\r\n</p><p>namespace&nbsp;app\\index\\controller;\r\n</p><p>use&nbsp;think\\Controller;\r\n</p><p>use&nbsp;think\\facade\\View;\r\n</p><p>class&nbsp;Demo7&nbsp;extends&nbsp;Controller\r\n</p><p>{\r\n</p><p>&nbsp;&nbsp;&nbsp;//直接将内容输出到页面，不通过模板\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test1()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$content&nbsp;=&nbsp;\'&lt;h3&nbsp;style=\"color:yellow;\"&gt;知丫仔&lt;/h3&gt;\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//return&nbsp;$this-&gt;display($content);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//更加倾向于第二种\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//return&nbsp;$this-&gt;view-&gt;display($content);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//$this-&gt;view&nbsp;===&nbsp;new&nbsp;View();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//静态代理\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;View::display($content);\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//使用视图将数据进行输出：fetch()\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test2()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//模板变量赋值：assign()\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//1、普通变量\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign(\'name\',\'Peter&nbsp;Zhu\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign(\'age\',99);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//批量赋值\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign([\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'sex\'=&gt;\'男\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'salary\'=&gt;666\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;]);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//2、array\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign(\'goods\',[\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'id\'=&gt;1,\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'name\'=&gt;\'手机\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'model\'=&gt;\'meta10\',\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\'price\'=&gt;999\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;]);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//3、object对象\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$obj&nbsp;=&nbsp;new&nbsp;\\stdClass();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$obj-&gt;course&nbsp;=&nbsp;\'PHP\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$obj-&gt;lecture&nbsp;=&nbsp;\'Peter&nbsp;zhu\';\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign(\'info\',$obj);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//4、const常量\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;define(\'SITE_NAME\',\'知丫仔\');\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//在模板中输出数据\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//模板默认的目录位于当前模块的view目录，模板文件默认位于以当前控制器命名的目录\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;view-&gt;fetch();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//常用的模板标签\r\n</p><p>&nbsp;&nbsp;&nbsp;public&nbsp;function&nbsp;test3()\r\n</p><p>&nbsp;&nbsp;&nbsp;{\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$data&nbsp;=&nbsp;\\app\\model\\Student::all();\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$this-&gt;view-&gt;assign(\'data\',$data);\r\n</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$this-&gt;view-&gt;fetch();\r\n</p><p>&nbsp;&nbsp;&nbsp;}\r\n</p><p>&nbsp;&nbsp;&nbsp;//分页\r\n</p><p>&nbsp;&nbsp;&nbsp;public&n